$(function () {
    $('form[name="form"]').submit(function (event) {
        event.preventDefault();

        var form = $(this);
        var action = form.attr("data-action");
        var data = form.serialize();

        $.ajax({
            url: action,
            type: "post",
            data: data,
            dataType: "json",
            beforeSend: function (load) {
                ajax_load("open");
            },
            success: function (result) {
                ajax_load("close");

                if (result.success === true) {
                    window.location.href = result.redirect.url;
                } else {
                    var view = '<div class="message ' + result.message_type + '">' + result.message + '</div>';
                    $('.message_box').removeClass('d-none').html(view);
                    $('.message_box').effect('bounce');
                }
            }
        });

        function ajax_load(action) {
            ajax_load_div = $(".ajax_load");

            if (action === "open") {
                ajax_load_div.fadeIn(200).css("display", "flex");
            }

            if (action === "close") {
                ajax_load_div.fadeOut(200);
            }
        }
    });
});
